import { wait } from "@testing-library/user-event/dist/utils";
import { takeLatest, call, all } from "redux-saga/effects";
import {
    SEARCH_USERS_FAIL,
    SEARCH_USERS_REQ,
    SEARCH_USERS_SUCEESS,
} from "./action";
function getUsers(params = "") {
    return axios.get("http://localhost:8080/users" + params);
}

//이 함수는 사용자 검색 액션 요청에 따라 실행되는 함수로
//이 함수를 통해 액션을 dispatch(전달? 실행?) 해서 api를 호출하여
//그 결과를 전달할 예정
function* searchRequest(action) {
    const userData = yield call(getUsers, action.params);

    try {
        yield put({ type: SEARCH_USERS_SUCEESS, data: userData.data });
    } catch (error) {
        yield put({ type: SEARCH_USERS_FAIL, data: error.response.data });
    }
}

function* waitSearchReq() {
    //yield키워드는 다음 동작을 제어하는 의미를 가진 es6문법
    //takeLatest는 가장 마지막에 실행된 액션을 감지하는 것
    yield takeLatest(SEARCH_USERS_REQ);
}

export default function* userSaga() {
    yield all([waitSearchReq()]);
}
